-- Criação da tabela facilitador_tech
CREATE TABLE facilitador_tech (
  id_facilitador_tech SERIAL PRIMARY KEY,
  nome VARCHAR(100),
  cpf VARCHAR(11),
  endereco VARCHAR(200),
  cidade VARCHAR(100),
  estado VARCHAR(100),
  telefone VARCHAR(20),
  email VARCHAR(100),
  formacao VARCHAR(200),
  area_atuacao VARCHAR(100)
);
